import React from "react";

const products = [
  {
    id: 1,
    title: "Workwear & PPE",
    image: "/images/placeholder/workwear.jpg",
  },
  {
    id: 2,
    title: "Corporate Apparel",
    image: "/images/placeholder/apparel.jpg",
  },
  {
    id: 3,
    title: "Branded Mugs",
    image: "/images/placeholder/mug.jpg",
  },
  {
    id: 4,
    title: "Promotional Bottles",
    image: "/images/placeholder/bottle.jpg",
  },
  {
    id: 5,
    title: "Gift Packaging",
    image: "/images/placeholder/packaging.jpg",
  },
  {
    id: 6,
    title: "Corporate Headwear",
    image: "/images/placeholder/cap.jpg",
  },
];

const ProductShowcase = () => {
  return (
    <section className="bg-white py-16">
      <div className="max-w-7xl mx-auto px-4">
        
        {/* Section Title */}
        <div className="mb-10 text-center">
          <h2 className="text-3xl md:text-4xl font-bold font-dinbek text-reshow-dark">
            Our Branding Solutions
          </h2>
          <p className="mt-3 font-dosis text-gray-600 max-w-2xl mx-auto">
            A snapshot of our corporate branding, apparel, promotional gifts, and
            professional workwear solutions.
          </p>
        </div>

        {/* Image Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {products.map((item) => (
            <div
              key={item.id}
              className="group relative overflow-hidden rounded-xl border bg-gray-50 shadow-sm hover:shadow-lg transition"
            >
              <img
                src={item.image}
                alt={item.title}
                className="h-64 w-full object-cover transition-transform duration-300 group-hover:scale-105"
                onError={(e) => {
                  e.target.src = '/images/placeholder.jpg';
                }}
              />

              {/* Overlay */}
              <div className="absolute inset-0 bg-black/40 opacity-0 group-hover:opacity-100 transition flex items-center justify-center">
                <h3 className="text-white text-lg font-semibold font-dinbek">
                  {item.title}
                </h3>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default ProductShowcase;
