import React from "react";

const products = [
  {
    id: 1,
    title: "Workwear & PPE",
    image: "/images/placeholder/workwear.jpg",
  },
  {
    id: 2,
    title: "Corporate Apparel",
    image: "/images/placeholder/apparel.jpg",
  },
  {
    id: 3,
    title: "Branded Mugs",
    image: "/images/placeholder/mug.jpg",
  },
  {
    id: 4,
    title: "Promotional Bottles",
    image: "/images/placeholder/bottle.jpg",
  },
  {
    id: 5,
    title: "Gift Packaging",
    image: "/images/placeholder/packaging.jpg",
  },
  {
    id: 6,
    title: "Corporate Headwear",
    image: "/images/placeholder/cap.jpg",
  },
];

const ProductShowcase = () => {
  // Duplicate products for seamless loop
  const duplicatedProducts = [...products, ...products, ...products];

  return (
    <section className="bg-white py-16 px-4 overflow-hidden">
      <div className="max-w-7xl mx-auto">
        
        {/* Section Title */}
        <div className="mb-10 text-center">
          <h2 className="text-3xl md:text-4xl font-bold font-dinbek text-reshow-dark">
            Our Branding Solutions
          </h2>
          <p className="mt-3 font-dosis text-gray-600 max-w-2xl mx-auto">
            A snapshot of our corporate branding, apparel, promotional gifts, and
            professional workwear solutions.
          </p>
        </div>

        {/* Ribbon Slideshow Container */}
        <div className="relative w-full overflow-hidden">
          <div className="flex animate-scroll">
            {duplicatedProducts.map((item, index) => (
              <div
                key={`${item.id}-${index}`}
                className="group relative flex-shrink-0 w-80 md:w-96 mx-4 rounded-xl border bg-gray-50 shadow-sm hover:shadow-lg transition-all"
              >
                <div className="h-64 w-full bg-gray-200 flex items-center justify-center overflow-hidden rounded-t-xl">
                  <img
                    src={item.image}
                    alt={item.title}
                    className="h-full w-full object-cover transition-transform duration-300 group-hover:scale-105"
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.parentElement.innerHTML = '<div class="h-full w-full flex items-center justify-center text-gray-400 font-dosis text-center px-4">' + item.title + '</div>';
                    }}
                  />
                </div>

                {/* Overlay */}
                <div className="absolute inset-0 bg-black/40 opacity-0 group-hover:opacity-100 transition flex items-center justify-center rounded-xl">
                  <h3 className="text-white text-lg font-semibold font-dinbek text-center px-4">
                    {item.title}
                  </h3>
                </div>

                {/* Title below image */}
                <div className="p-4">
                  <h3 className="text-lg font-semibold font-dinbek text-reshow-dark text-center">
                    {item.title}
                  </h3>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <style jsx>{`
        @keyframes scroll {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(calc(-100% / 3));
          }
        }

        .animate-scroll {
          animation: scroll 30s linear infinite;
          display: flex;
          width: fit-content;
        }

        .animate-scroll:hover {
          animation-play-state: paused;
        }

        @media (max-width: 768px) {
          .animate-scroll {
            animation-duration: 20s;
          }
        }
      `}</style>
    </section>
  );
};

export default ProductShowcase;
