import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const HeroWelcome = () => {
  const text = "Trusted Zimbabwean company specialising in corporate branding, promotional gifts, corporate apparel, and branding solutions.";
  const [displayText, setDisplayText] = useState("");
  const [index, setIndex] = useState(0);

  useEffect(() => {
    if (index < text.length) {
      const timeout = setTimeout(() => {
        setDisplayText((prev) => prev + text[index]);
        setIndex(index + 1);
      }, 50);
      return () => clearTimeout(timeout);
    }
  }, [index, text]);

  return (
    <section className="relative bg-white pt-20 pb-40">
      <div className="max-w-7xl mx-auto px-6 text-center">

        {/* CENTER IMAGE */}
        <div className="flex justify-center mb-10">
          <img
            src="/images/placeholder/hero-image.jpg"
            alt="Reshow Branding"
            className="w-72 md:w-96 object-contain"
            onError={(e) => {
              e.target.src = '/images/placeholder.jpg';
            }}
          />
        </div>

        {/* COMPANY NAME */}
        <h1 className="text-4xl md:text-6xl font-bold font-dinbek text-reshow-dark mb-6">
          Reshow Investments (Pvt) Ltd
        </h1>

        {/* TAGLINE */}
        <p className="text-2xl md:text-3xl font-dosis text-reshow-dark mb-8">
          Name it we brand it.
        </p>

        {/* TYPEWRITER TEXT */}
        <p className="text-lg md:text-2xl font-dosis text-gray-600 max-w-4xl mx-auto mb-10 min-h-[4rem]">
          {displayText}
          {index < text.length && (
            <span className="animate-pulse text-reshow-red">|</span>
          )}
        </p>

        {/* CTA BUTTONS */}
        <div className="flex flex-col sm:flex-row gap-4 justify-center">
          <Link
            to="/services"
            className="bg-reshow-red text-white px-8 py-3 rounded-lg font-dosis font-semibold hover:bg-reshow-dark-red transition-colors"
          >
            Request Quote
          </Link>
          <Link
            to="/contact"
            className="bg-transparent border-2 border-reshow-red text-reshow-red px-8 py-3 rounded-lg font-dosis font-semibold hover:bg-reshow-red hover:text-white transition-colors"
          >
            Contact Us
          </Link>
        </div>
      </div>

      {/* Wave SVG at the bottom */}
      <div className="absolute bottom-0 left-0 w-full overflow-hidden">
        <svg
          viewBox="0 0 1200 120"
          preserveAspectRatio="none"
          className="relative block w-full h-20"
          style={{ transform: 'rotate(180deg)' }}
        >
          <path
            d="M0,0V46.29c47.79,22.2,103.59,32.17,158,28,70.36-5.37,136.33-33.31,206.8-37.5C438.64,32.43,512.34,53.67,583,72.05c69.27,18,138.3,24.73,209.4,13.08,36.15-6,69.85-17.84,104.45-29.34C989.49,25,1113-14.29,1200,52.47V0Z"
            opacity=".25"
            fill="#ED1C24"
          ></path>
          <path
            d="M0,0V15.81C13,36.92,27.64,56.86,47.69,72.05,99.41,111.27,165,111,224.58,91.58c31.15-10.15,60.09-26.07,89.67-39.8,40.92-19,84.73-46,130.83-49.67,36.26-2.85,70.9,9.42,98.6,31.56,31.77,25.39,62.32,62,103.63,73,40.44,10.79,81.35-6.69,119.13-24.28s75.16-39,116.92-43.05c59.73-5.48,111.28,30.13,172.56,47.07,57.74,16.04,126.67,21.67,178.74,8.19C1200,52.47,1200,0,1200,0H0Z"
            opacity=".5"
            fill="#ED1C24"
          ></path>
          <path
            d="M0,0V5.63C149.93,59,314.09,71.32,475.83,42.57c43-7.64,84.23-20.12,127.61-26.46,59-8.63,112.48,12.24,165.56,35.4C827.93,77.22,886,95.24,951.2,90c86.53-7,172.46-45.71,248.8-84.81V0H0Z"
            fill="#ED1C24"
          ></path>
        </svg>
      </div>
    </section>
  );
};

export default HeroWelcome;
