import React, { useState } from 'react';

const Gallery = () => {
  // Placeholder images - replace with actual images from server
  const [images] = useState([
    { id: 1, src: '/images/placeholder.jpg', alt: 'Gallery Image 1' },
    { id: 2, src: '/images/placeholder.jpg', alt: 'Gallery Image 2' },
    { id: 3, src: '/images/placeholder.jpg', alt: 'Gallery Image 3' },
    { id: 4, src: '/images/placeholder.jpg', alt: 'Gallery Image 4' },
    { id: 5, src: '/images/placeholder.jpg', alt: 'Gallery Image 5' },
    { id: 6, src: '/images/placeholder.jpg', alt: 'Gallery Image 6' },
  ]);

  const [selectedImage, setSelectedImage] = useState(null);

  return (
    <div className="pt-20">
      {/* Hero */}
      <section className="bg-reshow-white py-16 px-4">
        <div className="max-w-4xl mx-auto text-center">
          <h1 className="text-5xl font-bold font-dinbek mb-6 text-reshow-dark">Gallery</h1>
          <p className="text-lg font-dosis text-gray-700">
            Explore our portfolio of branding solutions and corporate products
          </p>
        </div>
      </section>

      {/* Gallery Grid */}
      <section className="py-16 px-4 bg-gray-50">
        <div className="max-w-6xl mx-auto">
          <div className="grid md:grid-cols-3 gap-6">
            {images.map(image => (
              <div
                key={image.id}
                onClick={() => setSelectedImage(image)}
                className="bg-white rounded-lg overflow-hidden shadow-sm hover:shadow-lg transition-shadow cursor-pointer"
              >
                <div className="aspect-square bg-gray-200">
                  <img
                    src={image.src}
                    alt={image.alt}
                    className="w-full h-full object-cover"
                    onError={(e) => {
                      e.target.src = '/images/placeholder.jpg';
                    }}
                  />
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Image Modal */}
      {selectedImage && (
        <div
          className="fixed inset-0 bg-black bg-opacity-75 z-50 flex items-center justify-center p-4"
          onClick={() => setSelectedImage(null)}
        >
          <div className="max-w-4xl max-h-full">
            <img
              src={selectedImage.src}
              alt={selectedImage.alt}
              className="max-w-full max-h-full object-contain"
            />
            <button
              onClick={() => setSelectedImage(null)}
              className="absolute top-4 right-4 text-white text-4xl hover:text-gray-300"
            >
              Ã—
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Gallery;
