import React, { useState, useEffect } from 'react';
import api from '../utils/api';

const Services = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [loading, setLoading] = useState(true);

  const categories = ['all', 'Apparel', 'Headwear', 'Promotional Gifts', 'PPE Wear'];

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const res = await api.get('/products');
      setProducts(res.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching products:', error);
      // Set empty array if server is not available
      setProducts([]);
      setLoading(false);
    }
  };

  const addToCart = (product) => {
    setCart([...cart, { ...product, quantity: 1 }]);
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  const updateQuantity = (productId, quantity) => {
    if (quantity <= 0) {
      removeFromCart(productId);
      return;
    }
    setCart(cart.map(item => 
      item.id === productId ? { ...item, quantity } : item
    ));
  };

  const filteredProducts = selectedCategory === 'all'
    ? products
    : products.filter(p => p.category === selectedCategory);

  const handleRequestQuote = async () => {
    if (cart.length === 0) {
      alert('Your cart is empty');
      return;
    }

    const name = prompt('Enter your name:');
    const email = prompt('Enter your email:');
    const phone = prompt('Enter your phone (optional):');
    const company = prompt('Enter your company (optional):');
    const message = prompt('Additional message (optional):');

    if (!name || !email) {
      alert('Name and email are required');
      return;
    }

    try {
      await api.post('/quotes', {
        name,
        email,
        phone: phone || '',
        company: company || '',
        products: cart.map(item => ({
          productId: item.id,
          productName: item.name,
          quantity: item.quantity
        })),
        message: message || ''
      });

      alert('Quote request submitted successfully! We will contact you soon.');
      setCart([]);
    } catch (error) {
      alert('Failed to submit quote request. Please try again.');
    }
  };

  return (
    <div className="pt-20">
      {/* Hero */}
      <section className="bg-reshow-white py-16 px-4">
        <div className="max-w-4xl mx-auto text-center">
          <h1 className="text-5xl font-bold font-dinbek mb-6 text-reshow-dark">Our Services</h1>
          <p className="text-lg font-dosis text-gray-700">
            Comprehensive branding solutions for your organisation
          </p>
        </div>
      </section>

      {/* Cart Sidebar */}
      {cart.length > 0 && (
        <div className="fixed right-0 top-20 bg-white shadow-lg p-6 w-80 max-h-[calc(100vh-5rem)] overflow-y-auto z-40">
          <h3 className="text-xl font-bold font-dinbek mb-4">Cart ({cart.length})</h3>
          <div className="space-y-4 mb-4">
            {cart.map(item => (
              <div key={item.id} className="border-b pb-4">
                <div className="flex justify-between items-start mb-2">
                  <div className="flex-1">
                    <p className="font-dosis font-semibold">{item.name}</p>
                    <p className="font-dosis text-sm text-gray-600">{item.category}</p>
                  </div>
                  <button
                    onClick={() => removeFromCart(item.id)}
                    className="text-red-500 hover:text-red-700"
                  >
                    Ã—
                  </button>
                </div>
                <div className="flex items-center gap-2">
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity - 1)}
                    className="px-2 py-1 border rounded"
                  >
                    -
                  </button>
                  <span className="font-dosis">{item.quantity}</span>
                  <button
                    onClick={() => updateQuantity(item.id, item.quantity + 1)}
                    className="px-2 py-1 border rounded"
                  >
                    +
                  </button>
                </div>
              </div>
            ))}
          </div>
          <button
            onClick={handleRequestQuote}
            className="w-full bg-reshow-red text-white py-3 rounded-lg font-dosis font-semibold hover:bg-reshow-dark-red transition-colors"
          >
            Request Quote
          </button>
        </div>
      )}

      {/* Category Filter */}
      <section className="py-8 px-4 bg-gray-50">
        <div className="max-w-6xl mx-auto">
          <div className="flex flex-wrap gap-4 justify-center">
            {categories.map(cat => (
              <button
                key={cat}
                onClick={() => setSelectedCategory(cat)}
                className={`px-6 py-2 rounded-lg font-dosis font-semibold transition-colors ${
                  selectedCategory === cat
                    ? 'bg-reshow-red text-white'
                    : 'bg-white text-reshow-dark hover:bg-gray-100'
                }`}
              >
                {cat.charAt(0).toUpperCase() + cat.slice(1)}
              </button>
            ))}
          </div>
        </div>
      </section>

      {/* Products Grid */}
      <section className="py-16 px-4 bg-reshow-white">
        <div className="max-w-6xl mx-auto">
          {loading ? (
            <div className="text-center py-12">
              <p className="font-dosis text-gray-700">Loading products...</p>
            </div>
          ) : filteredProducts.length === 0 ? (
            <div className="text-center py-12">
              <p className="font-dosis text-gray-700">No products found in this category.</p>
            </div>
          ) : (
            <div className="grid md:grid-cols-3 gap-8">
              {filteredProducts.map(product => (
                <div key={product.id} className="bg-white border rounded-lg overflow-hidden shadow-sm hover:shadow-md transition-shadow">
                  <div className="h-48 bg-gray-200 flex items-center justify-center">
                    <img
                      src={product.image || '/images/placeholder.jpg'}
                      alt={product.name}
                      className="w-full h-full object-cover"
                      onError={(e) => {
                        e.target.src = '/images/placeholder.jpg';
                      }}
                    />
                  </div>
                  <div className="p-6">
                    <span className="text-xs font-dosis text-gray-500 uppercase">{product.category}</span>
                    <h3 className="text-xl font-bold font-dinbek mt-2 mb-3 text-reshow-dark">{product.name}</h3>
                    <p className="font-dosis text-gray-700 mb-4">{product.description}</p>
                    <button
                      onClick={() => addToCart(product)}
                      className="w-full bg-reshow-dark text-white py-2 rounded-lg font-dosis font-semibold hover:bg-gray-800 transition-colors"
                    >
                      Add to Cart
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Services;
