import React from 'react';
import { Link } from 'react-router-dom';
import HeroWelcome from '../components/HeroWelcome';
import ProductShowcase from '../components/ProductShowcase';
import WhyChooseReshow from '../components/WhyChooseReshow';
import ProductCategoriesSection from '../components/ProductCategoriesSection';

const Home = () => {
  return (
    <div className="pt-20">
      {/* Hero Section */}
      <section className="bg-gradient-to-r from-reshow-red to-reshow-dark-red text-white py-20 px-4">
        <div className="max-w-6xl mx-auto">
          {/* Top Section - Company Intro */}
          <div className="text-center mb-12">
            <h1 className="text-5xl md:text-6xl font-bold font-dinbek mb-6">
              Reshow Investments (Pvt) Ltd
            </h1>
            <p className="text-2xl md:text-3xl font-dosis mb-8">
              Name it we brand it.
            </p>
            <p className="text-lg md:text-xl mb-10 opacity-95 max-w-2xl mx-auto">
              Trusted Zimbabwean company specialising in corporate branding, promotional gifts, 
              corporate apparel, and branding solutions.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link
                to="/services"
                className="bg-white text-reshow-red px-8 py-3 rounded-lg font-dosis font-semibold hover:bg-gray-100 transition-colors"
              >
                Request Quote
              </Link>
              <Link
                to="/contact"
                className="bg-transparent border-2 border-white text-white px-8 py-3 rounded-lg font-dosis font-semibold hover:bg-white hover:text-reshow-red transition-colors"
              >
                Contact Us
              </Link>
            </div>
          </div>

        </div>
      </section>

      {/* Why Choose Us */}
      <WhyChooseReshow />

      {/* Product Categories Section */}
      <ProductCategoriesSection />

      {/* Product Showcase */}
      <ProductShowcase />
    </div>
  );
};

export default Home;
