import React, { createContext, useState, useEffect } from 'react';
import api from '../utils/api';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let isMounted = true;

    // For admin panel: Don't auto-restore session - always require fresh login
    // Clear any stored credentials on page load to force login
    const isAdminRoute = window.location.pathname.startsWith('/admin');

    if (isAdminRoute) {
      // Always clear credentials for admin routes to force fresh login
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      if (isMounted) {
        setUser(null);
        setLoading(false);
      }
      return () => {
        isMounted = false;
      };
    }

    // For non-admin routes, we can allow some session restore if needed
    // But for now, we'll keep it simple - no auto-restore
    if (isMounted) {
      setUser(null);
      setLoading(false);
    }

    return () => {
      isMounted = false;
    };
  }, []);

  const login = async (email, password) => {
    try {
      const res = await api.post('/auth/login', { email, password });

      if (res.data.mustChangePassword) {
        return { mustChangePassword: true, userId: res.data.userId };
      }

      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      setUser(res.data.user);
      return { success: true, user: res.data.user };
    } catch (error) {
      throw error.response?.data || { error: 'Login failed' };
    }
  };

  const changePassword = async (userId, currentPassword, newPassword) => {
    try {
      const res = await api.post('/auth/change-password', {
        userId,
        currentPassword,
        newPassword
      });

      localStorage.setItem('token', res.data.token);
      localStorage.setItem('user', JSON.stringify(res.data.user));
      setUser(res.data.user);
      return { success: true, user: res.data.user };
    } catch (error) {
      throw error.response?.data || { error: 'Password change failed' };
    }
  };

  const logout = () => {
    // Clear all authentication data
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    // Force redirect to login page
    window.location.href = '/admin/login';
  };

  return (
    <AuthContext.Provider value={{ user, login, logout, changePassword, loading }}>
      {children}
    </AuthContext.Provider>
  );
};
