import React, { useEffect, useState } from "react";

const HeroWelcome = () => {
  const text = "Name it we brand it.";
  const [displayText, setDisplayText] = useState("");
  const [index, setIndex] = useState(0);

  useEffect(() => {
    if (index < text.length) {
      const timeout = setTimeout(() => {
        setDisplayText((prev) => prev + text[index]);
        setIndex(index + 1);
      }, 100);
      return () => clearTimeout(timeout);
    }
  }, [index, text]);

  return (
    <section className="relative bg-white pt-20 pb-40">
      <div className="max-w-7xl mx-auto px-6 text-center">

        {/* CENTER IMAGE */}
        <div className="flex justify-center mb-10">
          <img
            src="/images/placeholder/hero-image.jpg"
            alt="Reshow Branding"
            className="w-72 md:w-96 object-contain"
            onError={(e) => {
              e.target.src = '/images/placeholder.jpg';
            }}
          />
        </div>

        {/* TYPEWRITER TEXT */}
        <h1 className="text-4xl md:text-6xl font-bold font-dinbek text-reshow-dark mb-6 min-h-[3rem]">
          {displayText}
          {index < text.length && (
            <span className="animate-pulse">|</span>
          )}
        </h1>

        {/* SUB TEXT */}
        <p className="text-lg md:text-2xl font-dosis text-gray-600 max-w-4xl mx-auto">
          Trusted Zimbabwean company specialising in corporate branding,
          promotional gifts, corporate apparel, and branding solutions.
        </p>
      </div>

      {/* RED BACKGROUND AT BOTTOM */}
      <div className="absolute bottom-0 left-0 w-full h-32 bg-gradient-to-r from-reshow-red to-reshow-dark-red"></div>
    </section>
  );
};

export default HeroWelcome;
