import React from "react";

const MissionVision = () => {
  return (
    <section className="w-full bg-white py-16 overflow-hidden">
      <div className="max-w-7xl mx-auto px-4 grid grid-cols-1 lg:grid-cols-2 gap-0 relative">

        {/* OUR MISSION */}
        <div className="relative bg-gradient-to-br from-red-600 to-red-700 text-white p-12 flex items-center">
          {/* Diagonal Shape */}
          <div className="absolute top-0 right-0 w-24 h-full bg-red-800 transform -skew-x-12 origin-top-right"></div>

          <div className="relative z-10">
            <h2 className="text-3xl font-bold font-dinbek uppercase mb-4">
              Our Mission
            </h2>
            <p className="text-lg font-dosis leading-relaxed">
              To deliver <span className="font-semibold text-red-200">high-quality</span>,
              <span className="font-semibold text-red-200"> innovative</span>, and
              <span className="font-semibold text-red-200"> cost-effective</span> corporate
              branding and promotional solutions that exceed client expectations through
              professionalism and reliability.
            </p>
          </div>
        </div>

        {/* OUR VISION */}
        <div
          className="relative bg-cover bg-center flex items-center"
          style={{
            backgroundImage: "url('/images/placeholder/corporate-gifts.jpg')",
            backgroundSize: 'cover',
            backgroundPosition: 'center'
          }}
        >
          {/* Red Overlay */}
          <div className="absolute inset-0 bg-red-700/80"></div>

          {/* Diagonal Accent */}
          <div className="absolute top-0 left-0 w-24 h-full bg-red-800 transform skew-x-12 origin-top-left"></div>

          <div className="relative z-10 p-12 text-white">
            <h2 className="text-3xl font-bold font-dinbek uppercase mb-4">
              Our Vision
            </h2>
            <p className="text-lg font-dosis leading-relaxed">
              To be Zimbabwe's <span className="font-semibold text-red-200">leading provider</span>
              of bespoke corporate branding and promotional products, driven by
              <span className="font-semibold text-red-200"> innovation</span>,
              <span className="font-semibold text-red-200"> excellence</span>, and
              <span className="font-semibold text-red-200"> customer-focused service</span>.
            </p>
          </div>
        </div>

      </div>
    </section>
  );
};

export default MissionVision;
