import axios from 'axios';

// Ensure API_URL is properly formatted
let API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Validate and fix API_URL format
if (!API_URL) {
  API_URL = 'http://localhost:5000/api';
} else {
  // Remove any leading colons or slashes that might cause issues
  API_URL = API_URL.trim();
  
  // If it starts with :, it's missing protocol and hostname
  if (API_URL.startsWith(':')) {
    API_URL = `http://localhost${API_URL}`;
  } else if (!API_URL.startsWith('http://') && !API_URL.startsWith('https://')) {
    // If it doesn't start with http:// or https://, add it
    if (API_URL.startsWith('//')) {
      API_URL = `http:${API_URL}`;
    } else if (API_URL.includes('://')) {
      // Already has protocol, use as is
    } else {
      // No protocol, add http://localhost
      API_URL = `http://localhost:${API_URL.replace(/^:?/, '')}`;
    }
  }
}

// Ensure it ends with /api if it doesn't already
if (!API_URL.endsWith('/api')) {
  if (API_URL.endsWith('/')) {
    API_URL = `${API_URL}api`;
  } else {
    API_URL = `${API_URL}/api`;
  }
}

// Debug log (remove in production)
console.log('API Base URL:', API_URL);

const api = axios.create({
  baseURL: API_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000, // 10 second timeout
});

// Add token to requests
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Handle token expiration
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/admin/login';
    }
    return Promise.reject(error);
  }
);

export default api;
